package problemshack;
import java.util.Scanner;
public class loop1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	

		
		   
		      Scanner s1 = new Scanner(System.in); 
		      int N = s1.nextInt();
		      for(int i =1;i<11;i++)  {
		          int  answer = N * i;
		        System.out.println(N + " x " + i +  " = " +  answer); 
		      } 
		     
		     
		

	}

}
